using UnityEngine;using Newtonsoft.Json;public class StrConfig  {		/// <summary>		///字符表		/// <summary>		public int id { get; set; }		/// <summary>		///中文		/// <summary>		public string zh { get; set; }		/// <summary>		///英文		/// <summary>		public string en { get; set; }		/// <summary>		///日文		/// <summary>		public string jp { get; set; }		/// <summary>		///韩文		/// <summary>		public string kr { get; set; }		/// <summary>		///西语		/// <summary>		public string sp { get; set; }		/// <summary>		///德语		/// <summary>		public string ge { get; set; }		/// <summary>		///本地id		/// <summary>		public int localId { get; set; }		 		public static string configName = "StrConfig";		public static string Version { get { return config.version; } }		public static StrConfig [] Datas { get { return config.datas; } }		public static StrConfig config { get; set; }		public string version { get; set; }		public StrConfig [] datas { get; set; }		public static void Init()		{			config = JsonConvert.DeserializeObject<StrConfig>(jsonStr);		}		public static StrConfig Get(int id)		{			foreach (var item in config.datas)			{				if (item.id == id)				{ 					return item;				}			}			return null;		}		private static string jsonStr="{\"datas\":[{\"id\":1,\"zh\":\"aaa\"},{\"id\":2},{\"id\":3},{\"id\":4},{\"id\":5},{\"id\":6},{\"id\":7},{\"id\":8},{\"id\":9},{\"id\":10},{\"id\":11},{\"id\":12},{\"id\":13},{\"id\":14},{\"id\":15},{\"id\":16},{\"id\":17},{\"id\":18},{\"id\":19},{\"id\":20},{\"id\":21},{\"id\":22},{\"id\":23},{\"id\":24},{\"id\":25},{\"id\":26},{\"id\":27},{\"id\":28},{\"id\":29},{\"id\":30},{\"id\":31},{\"id\":32},{\"id\":33},{\"id\":34},{\"id\":35},{\"id\":36},{\"id\":37},{\"id\":38},{\"id\":39},{\"id\":40},{\"id\":41},{\"id\":42},{\"id\":43},{\"id\":44},{\"id\":45},{\"id\":46},{\"id\":47}],\"version\":\"20200331\"}";}